head.script:
        
		scwin.onpageload = function() {
			
		};
		
		scwin.onpageunload = function() {
			
		};
		
		scwin.trigger1_onclick = function(e) {
			var json = [
				{
					col1:'Name1',
					col2:'Value1',
					col3:'Option1'
				},
				{
					col1:'Name2',
					col2:'Value2',
					col3:'Option2'
				},
				{
					col1:'Name3',
					col2:'Value3',
					col3:'Option3'
				}
			];
			dataList1.setJSON( json );
		};
		
		// SysteMenu
		// - ColumnHide : 선택 컬럼 숨기기
		// - ColumnShowAll : 전체 컬럼 숨기기 해제
		// - ColumnFix : 선택 컬럼 틀고정
		// - ColumnUnfixAll : 전체 컬럼 틀고정 취소
		// - ColumnAdjustWidth : 선택 컬럼 크기 자동 맞추기
		// - ColumnAdjustAuto : 전체 컬럼 크기 자동 맞추기
		// - FoldAll : 전체 그룹 접기
		// - UnfoldAll : 전체 그룹 펼치기
		// - Group : 전체 그룹 해제
		// - Ungroup : 선택 컬럼 그룹 해제
		// - UngroupAll : 선택 컬럼 그룹

		// 컨텍스트 메뉴를 열기 전에 호출되어, 컨텍스트 메뉴를 구성할 수있게 합니다.
		// result
		// - false : 컴텍스트 메뉴가 열리지 않습니다.
		// - hideSystemMenu
		scwin.gridView1_oncontextopen = function(row,col) {
			// return false; // 컨텍스트 메뉴를 보여주지 않습니다.
			// return { hideSystemMenu: false } // 전체 시스템 메뉴를 보여주지 않습니다.
			return {
				hideSystemMenu: [ "ColumnHide" ], // 특정 항목의 시스템 메뉴를 보여주지 않습니다.
				appendMenu: [{
					label:"엑셀 다운로드",
					// 사용자 메뉴의 표시 문자열
					userMenuId:"ExcelDown",
					// 사용자 메뉴의 id, 해당 컨텍스트 메뉴가 클릭될때, 입력값으로 전달됨
					className:"userMenu"
					// 사용자 메뉴의 스타일 클래스 이름 
				}], // 시스템 메뉴 아래에, 사용자 메뉴를 추가합니다.
			};
		};
		
		
		/**
		*	컨텍스트 메뉴가 클릭 된 후 호출됩니다. 시스템 메뉴는 시스템 기능이 완료된 후 호출됩니다.
		*
		*	@param	row				: 컨텍스트 메뉴를 표시하기 위해 마우스 오른클릭한 셀의 행 인덱스
		*	@param	col					: 컨텍스트 메뉴를 표시하기 위해 마우스 오른클릭한 셀의 열 인덱스
		*	@param	userMenuId		: 클릭한 메뉴가 사용자 메뉴일 경우 이를 식별할 수 있는 ID
		*	@param	isSystemMenu	: 클릭한 메뉴가 시스템 메뉴인지 여부
		*	@param	systemMenu	: 클릭한 메뉴가 시스템 메뉴일 경우 이를 식별할 수 있는 ID(아래 SystemMenu 참조)
		*/
		scwin.gridView1_oncontextclick = function(row, col, userMenuId, isSystemMenu, systemMenu) {
			if(isSystemMenu){
				alert(systemMenu + " clicked at (" + col + "," + row + ")!");
			} else {
				alert(userMenuId + " clicked at (" + col + "," + row + ")!");
				
				//사용자 메뉴 중에서 ExcelDown 일 경우 엑셀다운로드 수행 
				if ( userMenuId == "ExcelDown" ){
					//엑셀다운로드 
					var options = {
					   fileName : "downLoadExcel.xlsx" //[default : excel.xlsx] options.fileName 값이 없을 경우 default값 세팅
					};
					com.data.downloadGridViewExcel(gridView1, options);
				}
				
			}
		};
